import org.junit.jupiter.api.Test;import static org.assertj.core.api.AssertionsForClassTypes.assertThat;public class BankAccountTest{    @Test    void canGetFirstName()    {        BankAccount firstName = new BankAccount();        String actual = firstName.getFirstName("Sam");        String expected = "Sam";        assertThat(actual).isEqualTo(expected);    }    @Test    void canGetLastName()    {       BankAccount lastName = new BankAccount();       String actual = lastName.getLastName("Greenfield - Lee");       String expected = "Greenfield - Lee";       assertThat(actual).isEqualTo(expected);    }    @Test    void canGetDateOfBirth()    {        BankAccount dateOfBirth = new BankAccount();        String actual = dateOfBirth.getDateOfBirth("11 December");        String expected = "11 December";        assertThat(actual).isEqualTo(expected);    }    @Test    void canGetAccountNumber()    {        BankAccount accountNumber = new BankAccount();        int actual = accountNumber.getAccountNumber(5500);        int expected = 5500;        assertThat(actual).isEqualTo(expected);    }    @Test    void canGetBalance()    {        BankAccount balance = new BankAccount();        int actual = balance.getBalance(760);        int expected = 760;        assertThat(actual).isEqualTo(expected);    }}